def conduct_ttests(df,ttest_df,healthy_samples,comp_samples,column_name):
    """
    Calculates pvalue from ttest and outputs to dataframe.
    """
    pval = stats.ttest_ind(
        df.loc[:,healthy_samples].T,
        df.loc[:,comp_samples].T,axis=0,
        equal_var=True,
        nan_policy='omit').pvalue
    
    ttest_df[column_name] = pval
    return ttest_df

